        int x = p.xpoints[i];
        int y = p.ypoints[i];
        if (x > maxx) {
            maxx = x;
        }
        if (x < minx) {
            minx = x;
        }
        if (y > maxy) {
            maxy = y;
        }
        if (y < miny) {
            miny = y;
        }
    }
    return new java.awt.Rectangle(minx, miny, maxx - minx, maxy - miny);
}
