            dw.writeInt(((java.awt.Color) (v)).getGreen());
            dw.writeInt(((java.awt.Color) (v)).getBlue());
        } else if (v instanceof java.lang.Boolean) {
            dw.writeString("Boolean");
            if (((java.lang.Boolean) (v)).booleanValue()) {
                dw.writeString("TRUE");
