            tracker.addImage(loadImage(fileName), CH.ifa.draw.util.Iconkit.ID);
        }
    } 
    fRegisteredImages.removeAllElements();

    try {
        tracker.waitForAll();
    } catch (java.lang.Exception e) {
    }
}
