/**
 * Determines if it is legal to move pCard on top of pPile,
 * i.e. if a king is moved to an empty pile or any other rank on
 * a card of immediately greater rank but of a different color.
 *
 * @param pCard
 * 		The card we wish to move
 * @param pPile
 * 		The desired destination pile
 * @return True if the move is legal
 * @pre pCard != null && pPile != null
 */
boolean canMoveTo(ca.mcgill.cs.stg.solitaire.cards.Card pCard, ca.mcgill.cs.stg.solitaire.model.TableauPile pPile) {
    assert (pCard != null) && (pPile != null);
    ca.mcgill.cs.stg.solitaire.cards.CardStack pile = aPiles.get(pPile);
    if (pile.isEmpty()) {
        return pCard.getRank() == ca.mcgill.cs.stg.solitaire.cards.Rank.KING;
    } else {
        return (pCard.getRank().ordinal() == (pile.peek().getRank().ordinal() - 1)) && (!pCard.getSuit().sameColorAs(pile.peek().getSuit()));
    }
}

