@java.lang.Override
public boolean isFoundationPileEmpty(ca.mcgill.cs.stg.solitaire.model.FoundationPile pPile) {
    return aFoundations.isEmpty(pPile);
}

/**
 *
 * @param pCard
 * 		The card to check
 * @param pIndex
 * 		The index of the pile to check
 * @return True if pIndex contains pCard
 * @pre pCard != null && pIndex != null
 */
boolean contains(ca.mcgill.cs.stg.solitaire.cards.Card pCard, ca.mcgill.cs.stg.solitaire.model.TableauPile pIndex) {
    assert (pCard != null) && (pIndex != null);
    for (ca.mcgill.cs.stg.solitaire.cards.Card card : aPiles.get(pIndex)) {
        if (card == pCard) {
            return true;
        }
    }
    return false;
}

